var my_uploadImage = function(fileObj, callBack) {
	var randomStr = randomString();
	var fileName  = fileObj.name;
	var temp = fileName.lastIndexOf('.');
	var suffix  = fileName.substring(temp);
	var storeAs = "ecc_server/richtext/"+randomStr+suffix;
	client.multipartUpload(storeAs, fileObj).then(function(result){
		callBack(storeAs);
	});
}
//同步删除富文本和oss图片
var deleteImage = function(fileKey) {
	$.deleOssFileByFileKey(fileKey);
	return true;
}
//生成随机字符串（用于图片名）
function randomString() {
	var stringArea = 'ABCDEFGHJKMNPQRSTWXYZabcdefhijkmnprstwxyz2345678';
	var maxPos = stringArea.length;
	var ramStr = '';
	for (i = 0; i < 10; i++) {
		ramStr += stringArea.charAt(Math.floor(Math.random() * maxPos));
	}
	return ramStr;
}